// Test for compound assignment operators
let x = 10;
console.log(x);

// Test += operator
x += 5;
console.log(x);

// Test -= operator  
x -= 3;
console.log(x);

// Test with expressions
let y = 2;
x += y * 3;
console.log(x);

// Test multiple compound assignments
let z = 100;
z += 10;
z -= 5;
z += 2;
console.log(z);

// Test with variables
let a = 7;
let b = 3;
a += b;
console.log(a);

b -= 2;
console.log(b);
