// Binary numbers with lowercase 'b'
let a = 0b1010;
console.log(a);

// Binary numbers with uppercase 'B'
let b = 0B1111;
console.log(b);

// Binary number from example
let c = 0b011100;
console.log(c);

// Small binary number
let d = 0b0;
console.log(d);

// Single bit
let e = 0b1;
console.log(e);

// Large binary number
let f = 0b11111111;
console.log(f);

// Binary with leading zeros
let g = 0b00001010;
console.log(g);

// Maximum 8-bit value
let h = 0B10101010;
console.log(h);
