// Números enteros
let integer = 42;
console.log(integer);

// Números flotantes
let float = 3.14159;
console.log(float);

// Strings
let string = "Hello World";
console.log(string);

// Booleanos
let trueValue = true;
let falseValue = false;
console.log(trueValue);
console.log(falseValue);

// Null
let nullValue = null;
console.log(nullValue);

// Arrays
let array = [1, 2, 3, "hello", true];
console.log(array[0]);
console.log(array[3]);
console.log(array[4]);

// Objetos
let obj = {
    name: "Alice",
    age: 25,
    active: true
};
console.log(obj.name);
console.log(obj.age);
console.log(obj.active);
