// Hexadecimal numbers with lowercase 'x'
let a = 0x10;
console.log(a);

// Hexadecimal numbers with uppercase 'X'
let b = 0X20;
console.log(b);

// Hexadecimal with uppercase letters
let c = 0xABCDEF;
console.log(c);

// Hexadecimal with lowercase letters
let d = 0xabcdef;
console.log(d);

// Hexadecimal with mixed case
let e = 0x123ABC;
console.log(e);

// Large hexadecimal number
let f = 0xFFFFFF;
console.log(f);

// Small hexadecimal number
let g = 0x0;
console.log(g);

// Single digit hexadecimal
let h = 0xF;
console.log(h);
