// Octal numbers with lowercase 'o'
let a = 0o10;
console.log(a);

// Octal numbers with uppercase 'O'
let b = 0O20;
console.log(b);

// Octal number from example
let c = 0o1234567;
console.log(c);

// Small octal number
let d = 0o0;
console.log(d);

// Single octal digit
let e = 0o7;
console.log(e);

// Maximum 3-digit octal
let f = 0o777;
console.log(f);

// Octal with leading zeros
let g = 0o0012;
console.log(g);

// All octal digits
let h = 0O01234567;
console.log(h);
