// Scientific notation with lowercase 'e'
let a = 1e5;
console.log(a);

// Scientific notation with uppercase 'E'
let b = 2E3;
console.log(b);

// Scientific notation with positive exponent
let c = 3.14e+2;
console.log(c);

// Scientific notation with negative exponent
let d = 5.67e-4;
console.log(d);

// Complex scientific notation
let e = 100.45e14;
console.log(e);

// Small number with negative exponent
let f = 1.23456E-10;
console.log(f);

// Integer with exponent
let g = 42e2;
console.log(g);

// Decimal with exponent
let h = 0.1e3;
console.log(h);
